/*Table structure for table `w_member_platform` */

DROP TABLE IF EXISTS `w_member_platform`;

CREATE TABLE `w_member_platform` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `mid` int(11) NOT NULL COMMENT '用户ID',
  `platform_id` int(11) NOT NULL COMMENT '商家平台ID',
  `first_time` int(11) NOT NULL COMMENT '第一次来店时间戳',
  `last_time` int(11) NOT NULL COMMENT '最后来店时间戳',
  `first_date` int(11) NOT NULL COMMENT '第一次来店时间',
  `last_date` int(11) NOT NULL COMMENT '最后来店时间',
  `en_count` int(11) NOT NULL DEFAULT '0' COMMENT '来此商家次数',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

