/*Table structure for table `w_order` */

DROP TABLE IF EXISTS `w_order`;

CREATE TABLE `w_order` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `sn` char(18) NOT NULL COMMENT '订单号',
  `mid` int(11) DEFAULT NULL COMMENT '用户Id',
  `native_price` decimal(16,2) DEFAULT NULL COMMENT '原价',
  `price` decimal(16,2) NOT NULL COMMENT '订单价格',
  `pay_type` int(11) NOT NULL COMMENT '支付类型 1:微信，2:线下',
  `status` int(11) NOT NULL DEFAULT '1' COMMENT '订单状态 -1:订单已经删除 1:已经下单，2,已支付，3:已经完成',
  `created_at` int(11) NOT NULL COMMENT '创建时间',
  `table_id` int(11) NOT NULL DEFAULT '0' COMMENT '桌号',
  `platform_id` int(11) NOT NULL COMMENT '平台ID',
  `shop_id` int(11) NOT NULL COMMENT '店铺ID',
  PRIMARY KEY (`id`),
  KEY `mid` (`mid`),
  KEY `shop_id` (`shop_id`)
) ENGINE=InnoDB AUTO_INCREMENT=225 DEFAULT CHARSET=utf8;

